import org.junit.jupiter.api.Test;
import secminhr.ChuonBinary;

import java.util.Arrays;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;

public class StringChuonBinaryTest {
    @Test
    public void Test_Empty() {
        String s = "";
        ChuonBinary binary = new ChuonBinary(s);
        assertArrayEquals(
            new Byte[] { 0x1C, 0x00 },
            binary.getBytes()
        );
    }

    @Test
    public void Test_a() {
        String s = "a";
        ChuonBinary binary = new ChuonBinary(s);
        assertArrayEquals(
            new Byte[] { 0x1C, 0x01, 0x61 },
            binary.getBytes()
        );
    }

    @Test
    public void Test_abcdefg() {
        String s = "abcdefg";
        ChuonBinary binary = new ChuonBinary(s);
        assertArrayEquals(
            new Byte[] { 0x1C, 0x07, 0x61, 0x62, 0x63, 0x64, 0x65, 0x66, 0x67},
            binary.getBytes()
        );
    }

    @Test
    public void Test_17382a() {
        String s = "";
        ChuonBinary binary = new ChuonBinary(s);
        Byte[] expected = new Byte[17386];
        Arrays.fill(expected, (byte) 0x61);
        expected[0] = 0x1C;
        expected[1] = (byte) 0xE6;
        expected[2] = (byte) 0x87;
        expected[3] = 0x01;

        assertArrayEquals(expected, binary.getBytes());

    }
}
